@js:
var base64Chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=';
function customAtob(encodedStr) {
    var cleanedStr = String(encodedStr).replace(/=+$/, '');
    var decodedStr = '';
    for (var i = 0, acc = 0, charCode, idx = 0; charCode = cleanedStr.charAt(idx++); ~charCode && (acc = i % 4 ? acc * 64 + charCode : charCode, i++ % 4) ? decodedStr += String.fromCharCode(0xff & acc >> (-2 * i & 6)) : 0) {
        charCode = base64Chars.indexOf(charCode);
    }
    return decodedStr;
}

function unicodeArrayToString(unicodeArray) {
    var resultStr = '';
    for (var i = 0; i < unicodeArray.length; i++) {
        resultStr += String.fromCharCode(unicodeArray[i]);
    }
    return decodeURIComponent(escape(resultStr));
}

function reverseArray(array) {
    return array.slice().reverse();
}

function xorDecode(base64Str1, base64Str2) {
    var decodedArray1 = customAtob(base64Str2).split('').map((char)=>{ return char.charCodeAt(0); });
    var reversedArray1 = reverseArray(decodedArray1);
    var decodedArray2 = customAtob(base64Str1).split('').map((char)=>{ return char.charCodeAt(0); });
    var xorArray = decodedArray2.map((value, index)=>{ return value ^ reversedArray1[index % reversedArray1.length]; });
    return unicodeArrayToString(xorArray);
}
match=src.match(/await _.+?\('(.+?)','(.+?)'\)/)
if (match) {result=result+xorDecode(match[1],match[2])}
else {result=result}
