function login(){}

function gets_key() {
    var servers = String(source.bookSourceUrl);
    const serverList = servers.split('\n');

    for (let server of serverList) {
        let key = String(cookie.getCookie(server)) || String(java.getCookie(server));
        if (key) {
            let keyParts = key.split('secretKey=');
            if (keyParts.length > 1) {
                key = keyParts[keyParts.length - 1];
                setArgument('key', key);
                return key;
            }
        }
    }

    return '';  
 }

// 填写密钥
function loginqt(){
    let key = gets_key()
    if (key && key != "") {
            java.toast("您已填写秘钥:"+key);
            return false
        }
    java.startBrowserAwait(getArgument('server')+'/check_key','登录晴天小说书源');
    key = gets_key()
    if (key){
        java.longToast('登录成功，秘钥：'+key);
        return true
        } else {
            java.toast('登录失败！！！！！');
            return false
            }
}

//登录番茄
function fq_login() {
    var cookies = cookie.getCookie('fanqienovel.com') || java.getCookie('fanqienovel.com')
    java.log(cookies)
    if (cookies && cookies != "") {
        java.toast("请不要重复登录，请先退出登录！")
        return false
    }
    try {
        java.startBrowserAwait("https://fanqienovel.com/", "登录")
    } catch (e) {
        java.toast(e)
    }
    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {}
    var cookies = cookie.getCookie("fanqienovel.com")
    if (!cookies || cookies == "") {
        java.toast("登录失败！")
        return false
    }
    java.toast("登录成功！")
    return true
}

//退出番茄
function logout() {
    cookie.removeCookie("fanqienovel.com");
    cookie.removeCookie("snssdk.com");
 //cookie.removeCookie(getArgument('server'));
    java.toast("退出登录成功");
}

//获取参数
function getArgument(key) {
    let open_argument = source.getVariable();
    return getArguments(open_argument,key);
}

//设置参数
function setArgument(key, value) {
    let open_argument = source.getVariable();
    open_argument = getArguments(open_argument,'');
    open_argument[key] = value;
    open_argument = JSON.stringify(open_argument);
    source.setVariable(open_argument);
    return open_argument;
}

