function get_search() {
    var search = getArgument('search');
    var datadist = {
            "-1": "阅读模式",
            "-2": "漫画模式",
            "0": "音频模式",
      };
    var tishi = '\n\n当前搜索模式：'+datadist[search]
    java.toast(tishi);
    return search
}

//设置搜索
function set_search(mode,name) {
        setArgument('search',mode);
        setArgument('tone_id',mode);
        java.toast('\n\n已切换至'+name);
}

//获取服务器
function get_server() {
    var server = getArgument('server');
    var tishi = '\n\n当前服务器：'+ server
    java.toast(tishi);
    return server
}


//设置服务器
function set_server() {
    var servers = String(source.bookSourceUrl);
    const serverList = servers.split('\n');
    const currentIndex = serverList.indexOf(getArgument('server'));
    const nextIndex = (currentIndex + 1) % serverList.length;
    server = serverList[nextIndex];
    setArgument('server',server);
    java.toast('\n\n'+server + '\n\n注意：切换服务器后需要在详情页刷新目录才能生效');
}


