function device_register(is_random) {
    let char, udid, model, url, data, res, header;
    if (is_random) {
        // 随机信息
        char = '0123456789ABCDEF';
        udid = "";
        for (let i = 0; i < 16; i++) {
            udid += char.charAt(Math.floor(Math.random() * char.length))
        }
        udid = udid.toLowerCase();
        char += 'GHIJKLMNOPQRSTUVWXYZ';
        model = "";
        for (let i = 0; i < 8; i++) {
            if (i == 3) {
                model += '-';
            }
            model += char.charAt(Math.floor(Math.random() * char.length));
        }
    } else {
        // 真机信息
        char = java.md5Encode(java.androidId())
        udid = char.slice(0, 16)//  + java.md5Encode(char).slice(0, 8)
        model = device
    }

    if (source.getLoginHeader() && !is_random && source.getLoginHeader().includes(device)) {
        java.toast("本设备已注册，无需重复注册")
        return false;
    }
    java.toast("开始进行设备注册: \n设备型号: " + model + "\n设备id: " + udid)
    java.log("番茄免费小说: 开始进行设备注册: \n设备型号: " + model + "\n设备id: " + udid)


    // 注册账号
    url = "https://i.snssdk.com/service/2/device_register/";
    data = JSON.stringify({
        "header": {
            "package": "com.dragon.read",
            "openudid": udid,
            "device_model": model
        }
    });
    try {
      res = post(url, TT(data));
    } catch (e) {
      java.toast("网络连接错误，请检查你的网络环境")
      throw new Error("network error")
    }
    header = "device_id=" + res.device_id + "&device_type=" + model + "&iid=" + res.install_id;

    source.putLoginHeader(header);
    java.toast("设备注册成功！");
    java.log("device_register success: " + header)
    return true;
}



