function get(tag,num) {
    const { java, source, cookie, cache } = this;
    var get = JSON.parse(source.getVariable());
    var Url = get['url'].replace(/www/, 'v4api') + '/app/v1/comic/filter/classify';
    var list = JSON.parse(java.ajax(Url)).data.classifyList;
    if (/theme/.test(tag)) {
        theme = list[0].list[num].tagId;
    }
    if (/cate/.test(tag)) {
        cate = list[1].list[num].tagId;
    }
    if (/status/.test(tag)) {
        status = list[2].list[num].tagId;
    }
    if (/zone/.test(tag)) {
        zone = list[3].list[num].tagId;
    }
    var e = eval(tag);
    return e;
}
function explore(title, url, Grow, Basis, bool) {
    style = {
        title:title,
        url:url,
        style:{
            layout_flexGrow:Grow,
            layout_flexBasisPercent:Basis,
            layout_wrapBefore: bool
        }
    };
    return style;
}
function Get(e) {
    const { java, source, cookie, cache } = this;
    var Variable = String(source.getVariable()).length > 0 ? source.getVariable() : '{}';
    var get = JSON.parse(Variable);
    return get[e];
}
function Put(data) {
    const { java, source, cookie, cache } = this;
    var Variable = String(source.getVariable()).length > 0 ? source.getVariable() : '{}';
    var get = JSON.parse(Variable);
    data = get['TC_to_SC'] == true ? java.t2s(data) : data;
    return JSON.parse(data);
}
function Map(e) {
    const { java, source, cookie, cache } = this;
    var infomap = source.getLoginInfoMap();
    var map = (infomap !== null && infomap.get(e)) ? infomap.get(e) : '';
    return String(map);
}
function Num(e) {
    e = String(e);
    var num = e.length > 8 ? (e.substring(0, e.length - 8) + "亿") : (e.length > 4 ? (e.substring(0, e.length - 4) + "万") : e);
    return num;
}
function sleep(milliseconds) {
    var start = new Date().getTime();
    while (new Date().getTime() - start < milliseconds) {
    }
}
function Checkwait(e) {
    while (e == '') {
        sleep(10000);
        return true;
    }
    return e;
}
function formatBytes(bytes, decimals) {
    decimals = decimals === undefined ? 2 : decimals;
    if (bytes === 0) return '0 Bytes';
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return `${(bytes / Math.pow(1024, i)).toFixed(decimals)} ${sizes[i]}`;
}

