ruleToc.chapterList:
@css:
#allCatalog>.catalog-volume>label,
#allCatalog>.catalog-volume>ul
@js:
list=[];
$=result;
for(i in $){
if(/volume-name/.test(String($[i]))){
			text = "🏮〖"+String($[i].text()).replace(/订阅本卷.*/g,"").replace(/免费.*/g,"免费").trim()+"〗🏮";
		 list.push({
				text: text,
				volume: true
			});			
		}

else{  
		$[i].select("li").forEach(a=>{		
 info =	String(a.select("a").attr("title")).replace(/.*时间：|章节/g,"");
 vip =/chapter-locked/.test(a.select("em"));
			text= a.select("a").text();
			href= a.select("a").attr("href");

				list.push({	
						text: text,
						href: href,
			    	info: info,
			    	vip: vip
					})
			})
	}
}
list
