bookSourceComment:
//完善发现,添加搜索接口by河鹅和
//优化搜索发现，书籍id大于9201的都不要可自行到书名规则进行修改
//目录乱序修复by折木
//目录乱序修复可通过设置书籍变量来开启，默认关闭
api=[]
api[0]=`https://www.aaccnn.com/e/search/,{
  "body": "keyboard=${key}",
  "method": "POST"
}`
api[1]=`https://www.aahhss.com/e/search/,{
  "body": "keyboard=${key}",
  "method": "POST"
}`
api[2]=`https://www.aakkrr.com/book/${key}<,/${page}>`
v=String(source.getVariable());
var v=v.match(/^\d+$/)?v:0;
result=api[v]

