ruleToc.chapterList:
@js:
function spArr(arr, num){
	let newArr = []
	for(let i = 0; i < arr.length;){
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}
let res = JSON.parse(result).data
let item_list = spArr(res["allItemIds"], 100)
let array = [], va = [""];//初始化卷数组
for(let i = 0; i < item_list.length; i ++){
	let response = java.ajax('https://novel.snssdk.com/api/novel/book/directory/detail/v1/?item_ids='+item_list[i]);
	let data = JSON.parse(response).data
	data.forEach((x) => {
		vo = x.volume_name;
		if(va[va.length-1].indexOf(vo)<0 &&
			vo!=""){
			va.push(vo);
			array.push({
				name: vo,
				volume: 1
				});
			}
		array.push({
			name: x.title,	url:'http://81.70.63.5:369/content?item_id='+x.item_id,
			info: ((Number(x.volume_name)==0?"第一卷：默认":x.volume_name)+' • '+x.chapter_word_number+'字'+' • '+java.timeFormat(x.first_pass_time*1000))
		})
	})
}

va.length<3?
array.filter($=>!$.volume):
array;

searchUrl:
@js:
function getUrl(key) {
  let isNumber = /^\d+$/.test(key);
  if (isNumber) {
    return `https://api5-normal-sinfonlineb.fqnovel.com/reading/bookapi/multi-detail/v/?aid=1967&iid=1&version_code=999&book_id={{key}}`;
  } else {
    return `https://api5-normal-lf.fqnovel.com/reading/bookapi/search/page/v/?passback={{(page-1)*50}}&query={{key}}$&iid=1&tab_type=3&channel=0&aid=1967&app_name=novelapp&version_code=99999&device_platform=linux&device_type=WSA3&language=zh&os_version=8.1.0`;
  }
}
getUrl(key)
