exploreUrl:
@js: var result = [];
java.toast("🔥动态获取发现中……");
var push=(title,url,size)=>result.push({
  	"title": title,
  	"url": "https://ntrstory.com?page={{page - 1 == 0 ? \"\": page}}",
  	"style": {
   "layout_flexGrow": 1,
   "layout_flexBasisPercent": size
   }
 });
var top = java.ajax("https://ntrstory.com");
if (top) {
form=org.jsoup.Jsoup.parse(top).select("ul.dropdown-menu a.text-item")
    for(i in form){
        title=form[i].text()
        url="#"
        size=0.25;
  url=String(url).replace("1","{{page}}")
        push(title,url,size)
    };
     } else { 
      java.toast("🚫发现获取失败……");
}
JSON.stringify(result);
