  //设置保存接口
function set(num){
	var show="设置成功！\n当前接口：",
	tips="\n接口已更改，长按发现列表刷新后生效。",
	msg=show+"\n"+data[num]+tips;
	java.longToast(msg);
	return source.setVariable(num);
	}
	//获取接口显示
function get(sz){
	var show="当前接口：\n";
	return java.longToast(show+data[sz]);
	}
//按接口获取网址
var url;
url=data[sz];
url=url[1];
url;

