function getSearchUrl(key, baseUrl) {
	 return baseUrl || "" + "/modules/article/search.php," + JSON.stringify({"charset": "gbk","method": "POST","body": `searchkey=${key}&submit=Search`})
}

function getCoverUrl(s) {
	 var ret = ""
	 if (Number(s)) ret = `/fengmian/${s.slice(0,-3)}/${s}/${s}s.jpg`
	 return ret
}

