exploreUrl:
@js: var result = [];
java.toast("🔥动态获取发现中……");
var push=(title,url,size)=>result.push({
  	"title": title,
  	"url": url,
  	"style": {
   "layout_flexGrow": 1,
   "layout_flexBasisPercent": size
   }
 });
var top = java.ajax("https://www.kanshuw.com/top/all_0_1.html");
var full = java.ajax("https://www.kanshuw.com/full/0_1.html")
if (top&&full) {
a=org.jsoup.Jsoup.parse(top).select(".row").eq(2);
a=org.jsoup.Jsoup.parse(a).select("a");
b=org.jsoup.Jsoup.parse(full).select(".row").eq(1);
b=org.jsoup.Jsoup.parse(b).select("a");
c=org.jsoup.Jsoup.parse(full).select(".container a")
push("排行总榜分类",null,1)
    for(i in a){
        title=a[i].text()
        url=a[i].attr("href")
        if (url.endsWith("_1.html")) {
        url=String(url).replace(/_1\.html$/,"_{{page}}.html")
        size=0.3;
        push(title,url,size)
    }};
    push("完本分类",null,1)
    for(i in b){
        title=b[i].text()
        url=b[i].attr("href")
        if (url.endsWith("_1.html")) {
        url=String(url).replace(/_1\.html$/,"_{{page}}.html")
        size=0.3;
        push(title,url,size)
    }};

    push("分类",null,1)
    for(i in c){
        title=c[i].text()
        url=c[i].attr("href")
        if(url.match(/\/list/)){
         url=url+"1.html"
         if (url.endsWith("1.html")) {
        url=String(url).replace(/1\.html$/,"{{page}}.html")
        size=0.3;
        push(title,url,size)
    }}};
      } else { 
      java.toast("🚫发现获取失败……");
}
JSON.stringify(result);
